import Title from './components/Title'
import './App.css';
import React, { useState } from 'react';
import EventList from './components/EventList';
import Overlay from './components/Overlay';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


function App() {
  const [overlayShow, setOverlayShow] = useState(false);

  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Bradley"s birthday party',
      location: 'FeatherHall Avenue',
      date: '20/6/2022',
      time: '15:00',
      type: '',
      description: 'Bring your own food and a sleeping bag'
    },
    {
      id: 2,
      title: 'Maya"s ',
      location: 'Usher Hall',
      date: '3/5/2022',
      time: '11:00',
      type: 'Education',
      description: 'Have your own laptop if possible'
    },
  ]);

  console.log(events)

  const deleteEventHandler = (id) => {
    setEvents((priorEvent) => {
      console.log(priorEvent);
      return priorEvent.filter((event) => {
        return id !== event.id;
      })
    })
  }




  return (
    <>

      <Container className='header_content'>
        <Title />
        <Button variant="primary" onClick={() => setOverlayShow(true)}>
          Create Event
        </Button>
      </Container>

      <Container>
        <EventList
          events={events}
          deleteEventHandler={deleteEventHandler}
        />
      </Container>


      {
        overlayShow && <Overlay />
      }
    </>
  );
}

export default App;
